var holon = require('./holon');

module.exports = {

  setup: function (app) {
    
  },

  find: function (params, cb) {
    holon.findAll(cb);
  },

  get: function (id, params, cb) {
    holon.read(id, cb);
  },

  create: function (data, params, cb) {
    holon.save(data, cb);
  },

  update: function (id, data, params, cb) {
    holon.save(data, cb);
  },

  remove: function (id, params, cb) {
    holon.db.delete(id, true, cb);
  },
};
