"use strict";

var feathers = require('feathers');
var ecstatic = require('ecstatic');

var isProd = require('utils').isProd;

module.exports = feathers()
  .configure(feathers.socketio())
  .use('/tasks', require('tasks-server'))
  .use(ecstatic({
    root: __dirname + '/../../../static',
    cache: (isProd ? 3600 : 0),
  }));
