"use strict";

var bb = require('bb');
var React = require('react');

module.exports = bb.Router.extend({
  routes: {
    "tasks": "all",
  },
  all: function () {
    var TaskList = require('tasks-client/TaskList');
    var taskList = new TaskList();
    var TaskListView = require('tasks-client/TaskListView');
    var taskListView = new TaskListView({ model: taskList });
    React.renderComponent(taskListView, document.querySelector('main'));
  },
});
    
