"use strict";

var React = require('react.backbone');

var TaskList = require('./TaskList');
var TaskView = require('./TaskView');

module.exports = React.createBackboneClass({
  render: function() {
    var taskViews = this.model().models.map(function (task) {
      return TaskView({ model: task });
    });
    return require('./TaskList.jsx').call(this, taskViews);
  },
  add: function (event) {
    var task = this.model().add({});
    console.log("task add", task);
  },
});
