"use strict";

var sql = require('sql');

require('validation/server');

module.exports = sql.Model.extend({
  tableName: 'tasks',

  initialize: function () {
    var self = this;
    this.on('saving', function () {
      var err = self.validate();
      if (err) { throw err; }
    });
  },

  validation: require('tasks-common/validation'),
});
