"use strict";

var expect = require('chai').expect;

var TaskList = require('../TaskList');

describe("TaskList", function () {
  var taskList = new TaskList();

  describe(".create", function () {
    beforeEach(function () {
      taskList.reset();
    });

    it("should create valid tasks", function (done) {
      taskList.create({
        'name': 'valid_name',
        'description': 'valid_description',
      }).then(function (task) {
        expect(task.id)
          .to.be.a('number');
        expect(task.get('name'))
          .to.be.a('string')
          .and.equal('valid_name');
        expect(task.get('description'))
          .to.be.a('string')
          .and.equal('valid_description');
        done();
      });
    });

    it("should not allow invalid data", function (done) {
      taskList.create({
        "name": "areallylongnamethatissolong",
        "description": "valid_description",
      }).then(function (data) {
        expect(false).to.be.true;
      }).catch(function (error) {
        expect(error).to.exist;
        expect(error.name).to.equal("Name must be at most 10 characters");
        done();
      });
    });
  });
});

